public class L389_FindTheDifference__ {
    public char findTheDifference(String s, String t) {
        HashMap<Character, Integer> hashMap = new HashMap<>();
        for (int i=0; i<s.length(); i++) {
            char tmp = s.charAt(i);
            if (hashMap.containsKey(tmp)) {
                hashMap.put(tmp, hashMap.get(tmp)+1);
            } else {
                hashMap.put(tmp, 1);
            }
        }

        char result = '0';
        for (int i=0; i<t.length(); i++) {
            char tmp = t.charAt(i);
            if (!hashMap.containsKey(tmp)) {
                result = tmp;
            } else {
                hashMap.put(tmp, hashMap.get(tmp)-1);
                if (hashMap.get(tmp) < 1) {
                    result = tmp;
                }
            }
        }
        return result;
    }

    /**
     * 也可以使用异或，相同字符抵消
     */
    public char findTheDifference_better(String s, String t) {
        int result = t.charAt(s.length());
        for (int i=0; i<s.length(); i++) {
            result += s.charAt(i);
            result -= t.charAt(i);
        }
        return (char) result;
    }

    public static void main(String[] args) {
        char theDifference = new L389_FindTheDifference__().findTheDifference(
                "",
                "");
        System.out.println(theDifference);
    }
}
